import 'dart:convert';import 'package:flutter/material.dart';import 'package:schoolworkspro_app/api/api_response.dart';import '../../../../api/repositories/lecturer/attendance_repository.dart';import '../../../../response/lecturer/attendance_leave_type_response.dart';class BookLeaveViewModel extends ChangeNotifier {  ApiResponse _leaveTypeResponse = ApiResponse.initial("Empty Data");  ApiResponse get leaveTypeResponse => _leaveTypeResponse;  List<Leave> _leave = <Leave>[];  List<Leave> get leave => _leave;  Future<void> fetchBookLeave() async {    _leaveTypeResponse = ApiResponse.initial("Loading");    notifyListeners();    try {      LeaveTypeResponse res = await AttendanceRepository().AttendanceleaveType();      print(res.success);      if (res.success == true) {        _leave = res.leave!;        _leaveTypeResponse = ApiResponse.completed(res.success.toString());        notifyListeners();      } else {        _leaveTypeResponse = ApiResponse.error(res.success.toString());      }    } catch (e) {      print("VM CATCH ERR :: " + e.toString());      _leaveTypeResponse = ApiResponse.error(e.toString());    }    notifyListeners();  }  // ApiResponse _allLeaveTypeResponse = ApiResponse.initial("Empty Data");  // ApiResponse get allLeaveTypeResponse => _allLeaveTypeResponse;  // List<Leave> _allLeaveData = <Leave>[];  // List<Leave> get allLeaveData => _allLeaveData;  //  // Future<void> fetchAllLeave() async {  //   _allLeaveTypeResponse = ApiResponse.initial("Loading");  //   notifyListeners();  //   try {  //     LeaveTypeResponse res = await AttendanceRepository().AttendanceallLeaveDataType();  //  //     print(res.success);  //     if (res.success == true) {  //       _allLeaveData = res.allLeaveData!;  //  //       _allLeaveTypeResponse = ApiResponse.completed(res.success.toString());  //       notifyListeners();  //     } else {  //       _allLeaveTypeResponse = ApiResponse.error(res.success.toString());  //